mapper.select:
select * from (select rownum rn,m.* from 
(select * from product where pr_mno>0 and pr_mno=#{pr_mno}
 order by pr_date desc) m) 
where rn between #{startRow} and #{endRow}

select * from(select rownum rn,o.* from 
(select * from product where pr_mno<0 and pr_mno=#{pr_mno}
 order by pr_date desc) o)
where rn between #{startRow} and #{endRow}

select * from (select rownum rn,a.* from (select * from product where pr_approve='y'

	and pr_item = #{pr_item}

order by pr_date desc) a) where rn between #{startRow} and #{endRow}

select nvl(count(*),0) from product where pr_approve='y'

	and pr_item = #{pr_item}

order by pr_date desc

select * from(select rownum rn,m.* from 
(select * from product where pr_mno>0 and pr_approve ='n' order by pr_date desc) m)
where rn between #{startRow} and #{endRow}

select * from(select rownum rn,o.* from 
(select * from product where pr_mno>0 and pr_approve ='y' order by pr_date desc) o)
where rn between #{startRow} and #{endRow}

select * from(select rownum rn,m.* from 
(select * from product where pr_mno<0 and pr_approve ='n' order by pr_date desc) m)
where rn between #{startRow} and #{endRow}

select * from(select rownum rn,o.* from 
(select * from product where pr_mno<0 and pr_approve ='y' order by pr_date desc) o)
where rn between #{startRow} and #{endRow}
